import React, { Fragment } from "react";
import PropTypes from "prop-types";
import Momemt from "react-moment";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const PostItem = props => {
  return (
    <div className="post bg-white p-1 my-1">
      <div>
        <a href={`/users/${props.post.user}`}>
          <img className="round-img" src={props.post.avatar} alt="avatar" />
          <h4>{props.post.name}</h4>
        </a>
      </div>
      <div>
        <p className="my-1">{props.post.text}</p>
        <p className="post-date">
          Posted on {<Moment format="YYYY/MM/DD">{props.post.date}</Moment>}
        </p>
        <button type="button" className="btn btn-light">
          <i className="fas fa-thumbs-up"></i>
          <span>{props.post.likes.length}</span>
        </button>
        <button type="button" className="btn btn-light">
          <i className="fas fa-thumbs-down"></i>
        </button>
        <a href="post.html" className="btn btn-primary">
          Discussion <span className="comment-count">2</span>
        </a>
        <button type="button" className="btn btn-danger">
          <i className="fas fa-times"></i>
        </button>
      </div>
    </div>
  );
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => {
  return {
    auth: state.auth
  };
};

export default connect(mapStateToProps, {})(PostItem);
