import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

const ProfileItem = props => {
  return (
    <div className="profile bg-light">
      <img
        className="round-img"
        src={props.profile.user.avatar}
        alt="avatar image"
      />
      <div>
        <h2>{props.profile.user.name}</h2>
        <p>
          {props.profile.status}{" "}
          {props.profile.comany && <span>at {props.profile.company}</span>}
        </p>
        <p className="my-1">
          {props.profile.location && <span>{props.profile.location}</span>}
        </p>
        <Link to={`/profiles/${props.profile._id}`} className="btn btn-primary">
          Visit Profile
        </Link>
      </div>
    </div>
  );
};

ProfileItem.propTypes = {
  profile: PropTypes.object.isRequired
};

export default ProfileItem;
