import * as types from "./types";
import uuid from "uuid";

export const setAlert = (msg, alertType) => {
  return async dispatch => {
    const id = uuid.v4();
    dispatch({
      type: types.SET_ALERT,
      payload: { msg, alertType, id }
    });
    setTimeout(() => {
      dispatch({
        type: types.REMOVE_ALERT,
        payload: id
      });
    }, 5000);
    try {
    } catch (error) {
      console.error(error.message);
    }
  };
};
